mapper.select:
select id, password_hash, nickname, email, role, created_at, updated_at
from user
where id = #{id}

select id, password_hash, nickname, email, role, created_at, updated_at
from user
where email = #{email}

select id, password_hash, nickname, email, role, created_at, updated_at
from user
where nickname = #{nickname}

select exists (
  select 1
  from user
  where email = #{email}
)

select exists (
  select 1
  from user
  where nickname = #{nickname}
)

select user.refresh_token
from user
where id = #{id}
